pragma solidity ^0.5.0;

import "./erc-1155/IERC1155.sol";
import "openzeppelin-solidity/contracts/token/ERC721/IERC721.sol";
import "./roles/OwnableOperatorRole.sol";
import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";

contract ERC20TransferProxy is OwnableOperatorRole {

    function erc20safeTransferFrom(IERC20 token, address _from, address _to, uint256 _value) external onlyOperator {
        require(token.transferFrom(_from, _to, _value), "failure while transferring");
    }
}
